import React, { useState } from 'react';
import './adoption.css';

const Adoption = () => {
  const [count, setCount] = useState(10); // Predefined count variable

  const pets = [
    {
      name: 'Bella',
      type: 'Dog',
      breed: 'Labrador Retriever',
      price: '$500'
    },
    {
      name: 'Milo',
      type: 'Cat',
      breed: 'Siamese',
      price: '$300'
    },
    {
      name: 'Charlie',
      type: 'Dog',
      breed: 'Poodle',
      price: '$450'
    }
  ];

  const handleAdoptPet = () => {
    if (count > 0) {
      setCount(count - 1);
    } else {
      alert('No more pets available for adoption!');
    }
  };

  return (
    <div className="inventory-container">
      <h2>Pet Inventory</h2>
      <p>Pets available for adoption: {count}</p>
      <div className="pet-cards">
        {pets.map((pet, index) => (
          <div className="pet-card" key={index}>
            <h3>{pet.name}</h3>
            <p>Type: {pet.type}</p>
            <p>Breed: {pet.breed}</p>
            <p>Price: {pet.price}</p>
            <button onClick={handleAdoptPet}>Adopt</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Adoption;
